query GetMinPrices(
  $bestPricesInput: BestPricesV2Input!
  $priceChartInput: PriceChartV3Input!
  $brand: Brand!
  $fetchPriceChart: Boolean!
  ) {
  best_prices_v2(
    input: $bestPricesInput
    brand: $brand
  ) {
    cheapest {
      ...priceFields        
    }
    cheapest_direct {
      ...priceFields        
    }
    cheapest_convenient {
      ...priceFields
    }
  }
  price_chart_v3 (
    input: $priceChartInput
    brand: $brand
  ) @include(if: $fetchPriceChart) {
    prices {
      price {
        value
        depart_date
        return_date
      }
      stats {
        depart_low
        depart_value
        return_low
        return_value
      }
    }
  }
}

fragment priceFields on Price {
  depart_date
  return_date
  value
  found_at
  signature
  convenient
  segments {
    transfers {
      duration_seconds
      visa_required
      night_transfer
      country_code
      at
      to 
    }
  }
}
